import React, { useState } from 'react';
import axios from 'axios';
import Container from 'react-bootstrap/Container';
import Candado from '../imgs/candado-abierto.png';


export default function CambiarContrasenia() {
    
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [error, setError] = useState('');

    const handlePasswordChange = async (e) => {
        e.preventDefault();
        try {
            // Realizar una solicitud a la API para actualizar la contraseña del usuario
            const response = await axios.put(`http://localhost:8000/usuarios/cambiar-contrasenia`, {
                email,
                newPassword: password,
            });
    
            // Verificar si la contraseña se actualizó correctamente
            if (response.status === 200) {
                // Redirigir al usuario a la página de inicio de sesión
                window.location.href = '/login';
            } else {
                setError('Hubo un error al intentar cambiar la contraseña');
            }
        } catch (error) {
            console.error('Error:', error);
            setError('Hubo un error al procesar la solicitud');
        }
    };
    

    return (
        <Container>
            <div className="row">
                <div className="col mt-3 pt-3 mb-3 pb-3">
                    <img src={Candado} alt="Desbloquear" style={{ width: 200, margin: 0 }} />
                </div>
                <div className="col mt-3 pt-3 mb-3 pb-3">
                    <div className="mx-auto">
                        <div style={{ width: 300 }} className="mx-auto">
                            <p className="h4">Cambiar Contraseña</p>
                            <form onSubmit={handlePasswordChange}>
                                <div className="form-floating mb-3 mt-3">
                                    <input
                                        type="password"
                                        className="form-control"
                                        placeholder="Ingrese su nueva contraseña"
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                    />
                                    <label htmlFor="password">Nueva Contraseña</label>
                                </div>
                                <div className="form-floating mb-3 mt-3">
                                    <input
                                        type="password"
                                        className="form-control"
                                        placeholder="Confirme su nueva contraseña"
                                        value={confirmPassword}
                                        onChange={(e) => setConfirmPassword(e.target.value)}
                                    />
                                    <label htmlFor="confirmPassword">Confirmar Contraseña</label>
                                </div>
                                <div className="form-floating mb-3 mt-3">
                                    <input type="submit" className="btn btn-primary" value="Cambiar Contraseña" />
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
            {error && <div className="alert alert-danger">{error}</div>}
        </Container>
    );
}
