import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Container from 'react-bootstrap/Container';
import Candado from '../imgs/candado-abierto.png';

export default function CambiarContrasenia(props) {
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [error, setError] = useState('');
    const [email, setEmail] = useState('');
    const [name, setName] = useState('');

    useEffect(() => {
        // Leer el correo electrónico de los parámetros de la URL
        const searchParams = new URLSearchParams(window.location.search);
        const correo = searchParams.get('correo');
        setEmail(correo);

        // Obtener el nombre del usuario usando el correo electrónico
        async function fetchUserName() {
            try {
                const response = await axios.get(`http://localhost:8000/usuarios/nombre?correo=${correo}`);
                setName(response.data.nombre);
            } catch (error) {
                console.error('Error fetching user name:', error);
            }
        }

        fetchUserName();
    }, []);

    const handlePasswordChange = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.put(`http://localhost:8000/usuarios/cambiar-contrasenia`, {
                email,
                newPassword: password,
            });
    
            if (response.status === 200) {
                window.location.href = '/login';
            } else {
                setError('Hubo un error al intentar cambiar la contraseña');
            }
        } catch (error) {
            console.error('Error:', error);
            setError('Hubo un error al procesar la solicitud');
        }
    };
    

    return (
        <Container>
            <div className="row">
                <div className="col mt-3 pt-3 mb-3 pb-3">
                    <img src={Candado} alt="Desbloquear" style={{ width: 200, margin: 0 }} />
                </div>
                <div className="col mt-3 pt-3 mb-3 pb-3">
                    <div className="mx-auto">
                        <div style={{ width: 300 }} className="mx-auto">
                            <p className="h4">Cambiar Contraseña</p>
                            <p>Nombre: {name}</p>
                            <form onSubmit={handlePasswordChange}>
                                {/* Resto del formulario */}
                            </form>
                        </div>
                    </div>
                </div>
            </div>
            {error && <div className="alert alert-danger">{error}</div>}
        </Container>
    );
}
